#ğŸ”¹ This verifies the integrity of each result file and gives a quick overview of test outcomes.
#echo "ğŸ“„ Summary of merged test result files:"
#for file in "$MERGE_DIR"/*-result.json; do
#  echo "ğŸ“„ $file"
#  jq -r '.name, .status' "$file" || echo "âš ï¸ Invalid JSON in $file"
#done

#----------------------------------------------------------------------------------------------------------------------------------------------
#ğŸ”¹ Count how many result and container JSON files exist.
#ğŸ”¹ 2>/dev/null: Silently handles cases when no matching files exist.
echo "ğŸ“¦ Total result files: $(ls "$MERGE_DIR"/*-result.json 2>/dev/null | wc -l)"
echo "ğŸ“¦ Total container files: $(ls "$MERGE_DIR"/*-container.json 2>/dev/null | wc -l)"

